import Button from 'react-bootstrap/Button';
import '../Styles/index.css'
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useState } from 'react';
import { createContext } from 'react';
import { NavLink } from 'react-router-dom';

function Header() {
  // const [img, setImg] = useState("");
  // const inputEvent = (event) => {
  //   const data = event.target.value;
  //   setImg(data);
  // }
  
  // const con = createContext();
  // <con.Provider>
  // </con.Provider>

  return (
    <Navbar expand="lg" className="bg-body-tertiary">
      <Container fluid>
        <Navbar.Brand href="#">fukatkhau.com</Navbar.Brand>
        <Navbar.Toggle style={{zIndex:"1000"}}/>
        <div>
        <Navbar.Collapse id="navbarScroll" >
          <div className='px-2 py-3'>
            <Nav
              className="px-2 pt-3 py-2 py-lg-0 navasc"
              style={{ maxHeight: '200px' }}
              navbarScroll
            >
              <NavLink to="/" className="nav-link">Home</NavLink>
              <NavLink to="/Components/About" className="nav-link">About</NavLink>
              <NavLink to="/Components/Contact" className="nav-link">Contact</NavLink>
            </Nav>
          </div>
            <Form className="d-flex">
              <Form.Control
                name="query"
                type="search"
                placeholder="Search"
                className="me-2"
                aria-label="Search"
              />
              <Button variant="outline-success" className='searchbtn'>Search</Button>
            </Form>

          
        </Navbar.Collapse>
        </div>
      </Container>
    </Navbar>
  );
}

export default Header;
